frappe.ui.form.on('Item', {
	validate(frm) {
	    var ncm = frm.fields_dict.nf_ncm.get_value();
	    if (ncm) {
	        frm.fields_dict.nf_ncm.set_value(formataNCM(ncm));
	        if (frm.fields_dict.nf_ncm.get_value().length > 10) {
	            msgprint('C칩digo NCM Inv치lido');
	            frappe.validated = false;
	        }
	    }
	}
})

function formataNCM(ncm){
    return ncm.replace(/[^\d]/g, "").replace(/(\d{4})(\d{2})(\d{2})/, "$1.$2.$3");
}

let mask;

jQuery.getScript("https://unpkg.com/imask", () => {
    mask = IMask.createMask({
        mask: "+55 00 0000[0]-0000"
    })
})

